public interface ISORequestListener {
    public boolean process (ISOSource source, ISOMsg m);
}

public class EchoHandler extends Log
     implements ISORequestListener
{
     public boolean process (ISOSource source, ISOMsg m) {
         try {
             if ("0800".equals (m.getMTI())) {
                 m.setResponseMTI ();
                 m.set (39, "00");
                 source.send (m);
             }
         } catch (Exception e) {
             warn ("echo-handler", e);
         }
         return true;
     }
}

