        super ();
        DirPoll dp = new DirPoll ();
        dp.setLogger (logger, "dir-poll");
        dp.setPath ("/tmp/dirpoll");
        dp.createDirs ();
        dp.setProcessor (this);
        new Thread (dp).start ();
    }
    public byte[] process (String name, byte[] b) {
        return ("request: " + name + " content="+ new String (b)).getBytes();
    }
}

