    public QTest () {
        super();
        state = -1;
        log = Log.getLog(Q2.LOGGER_NAME, "qtest");
        log ("constructor");
    }
    public void init () {
        log ("init");
        state = STARTING;
    }
    public void start() {
        log ("start");
        state = STARTED;
        new Thread(this).start();
    }
    public void stop () {
        log ("stop");
        state = STOPPING;
    }
    public void destroy () {
        log ("destroy");
        state = STOPPED;
    }
    public void setTickInterval (long tickInterval) {
        this.tickInterval = tickInterval;
    }
    public long getTickInterval () {
        return tickInterval;
    }
    public void run () {
        for (int tickCount=0; running (); tickCount++) {
            log.info ("tick " + tickCount);
            ISOUtil.sleep (tickInterval);
        }
    }
    public int getState () {
        return state;
    }
    public String getStateAsString () {
        return state >= 0 ? stateString[state] : "Unknown";
    }
    public void setConfiguration (Configuration cfg) {                      
        debug = cfg.getBoolean("debug", true);
    }

    private boolean running() {
        return state == QBean.STARTING || state == QBean.STARTED;
    }
    private void log (String message) {
        if (debug)                                                          
            log (message);
    }
}

public void setLogger (String loggerName) {
    log = Log.getLog (loggerName, getClass().getName());
    setModified (true);
}
public void setRealm (String realm) {
    if (log != null)
        log.setRealm (realm);
}

public interface QPersist {
    public Element getPersist ();
    public boolean isModified ();
}

public void init () {
    if (state == -1) {
        setModified (false);
        try {
            initService();
            state = QBean.STOPPED;
        } catch (Throwable t) {
            log.warn ("init", t);
        }
    }
}

public synchronized void start() {
    if (state != QBean.DESTROYED &&
        state != QBean.STOPPED   &&
        state != QBean.FAILED)
       return;

    this.state = QBean.STARTING;

    try {
       startService();
    } catch (Throwable t) {
       state = QBean.FAILED;
       log.warn ("start", t);
       return;
    }
    state = QBean.STARTED;
}

public synchronized void stop () {
    if (state != QBean.STARTED)
       return;
    state = QBean.STOPPING;
    try {
       stopService();
    } catch (Throwable t) {
       state = QBean.FAILED;
       log.warn ("stop", t);
       return;
    }
    state = QBean.STOPPED;
}

public void destroy () {
    if (state == QBean.DESTROYED)
       return;
    if (state != QBean.STOPPED)
       stop();

    if (scheduledThreadPoolExecutor != null) {
        scheduledThreadPoolExecutor.shutdown();
        scheduledThreadPoolExecutor = null;
    }
    try {
       destroyService();
    }
    catch (Throwable t) {
       log.warn ("destroy", t);
    }
    state = QBean.DESTROYED;
}

